css:
.card {
    font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif;
    font-size: 18px;
    text-align: center;
    color: #2c3e50;
    background-color: #ffffff;
    line-height: 1.6;
    max-width: 600px;
    margin: 0 auto;
    padding: 20px;
}

.word {
    font-size: 32px;
    font-weight: 600;
    margin-bottom: 8px;
    color: #34495e;
}

.pos {
    font-size: 16px;
    color: #7f8c8d;
    font-style: italic;
    margin-bottom: 20px;
}

.definitions {
    text-align: left;
    margin: 20px 0;
    font-size: 18px;
    line-height: 1.7;
}

.pronunciation {
    text-align: left;
    margin: 15px 0;
    padding: 12px;
    background-color: #f8f9fa;
    border-left: 4px solid #3498db;
    border-radius: 4px;
}

.synonyms {
    text-align: left;
    margin: 15px 0;
    padding: 12px;
    background-color: #f8f9fa;
    border-left: 4px solid #2ecc71;
    border-radius: 4px;
}

.examples {
    text-align: left;
    margin: 15px 0;
    padding: 12px;
    background-color: #fff8e1;
    border-left: 4px solid #f39c12;
    border-radius: 4px;
    font-style: italic;
}

.etymology {
    text-align: left;
    margin: 15px 0;
    padding: 12px;
    background-color: #f3e5f5;
    border-left: 4px solid #9b59b6;
    border-radius: 4px;
    font-size: 16px;
}

.derived {
    text-align: left;
    margin: 15px 0;
    padding: 12px;
    background-color: #e8f5e8;
    border-left: 4px solid #27ae60;
    border-radius: 4px;
}

.note {
    text-align: center;
    margin-top: 20px;
    padding: 8px 12px;
    background-color: #ecf0f1;
    border-radius: 20px;
    font-size: 14px;
    color: #7f8c8d;
    font-style: italic;
}

strong {
    color: #2c3e50;
    font-weight: 600;
}

em {
    color: #7f8c8d;
    font-style: italic;
}

small {
    font-size: 14px;
    color: #95a5a6;
}

hr {
    border: none;
    height: 1px;
    background: linear-gradient(to right, transparent, #bdc3c7, transparent);
    margin: 20px 0;
}
